import styled from '@emotion/styled';

const ConditionalPage = () => {
  return (
    <>
      <h1>조건부 렌더링</h1>
      <MyDiv id="김제희" className="err" abc="1번">
        div1
      </MyDiv>
      <MyDiv id="김제희" className="b" abc="2번">
        div1
      </MyDiv>
      <Div2 qqq="1번">div1</Div2>
      <Div2 qqq="2번">div1</Div2>
      <Div3 bbb="3번">div1</Div3>
      <Div3 bbb="4번">div1</Div3>
      <Div4 isErr="2번">div1</Div4>
      <Div4 bbb="2번">div1</Div4>
      <Div5>div1</Div5>
      <MyBtn>일반</MyBtn>
      <MyBtn textBtn>텍스트</MyBtn>
      <MyBtn elevatedBtn>엘리베이티드</MyBtn>
    </>
  );
};

const Div5 = styled.div`
  border: 3px solid black;
  width: 200px;

  ${(props) => {
    return props.isErr
      ? { '&:hover': { backgroundColor: 'red' } }
      : { '&:hover': { backgroundColor: 'green' } };
  }}
`;

const Div4 = styled.div`
  border: 3px solid black;
  width: 200px;
  ${(props) => {
    return props.isErr === true
      ? {
          backgroundColor: 'red',
          color: 'blue',
        }
      : {
          backgroundColor: 'green',
          fontWeight: Blob,
        };
  }}
`;

const Div3 = styled.div`
  border: 3px solid black;
  width: 200px;
  ${(props) => {
    return props.bbb === '3번' ? { backgroundColor: 'pink', padding: '20px' } : { margin: '10px' };
  }}
`;

const Div2 = styled.div`
  border: 3px solid black;
  width: 200px;
  background-color: ${(a) => (a.qqq === '1번' ? 'red' : 'blue')};
`;

const MyDiv = styled.div`
  border: 3px solid black;
  width: 200px;
  text-align: center;
  ${(a) => (a.abc === '1번' ? `background-color:red` : `background-color:blue`)}
`;

export default ConditionalPage;
