    CommentBtn,
  CommentDate,
  CommentHeader,
  CommentInput,
  CommentInputWrap,
  CommentItem,
  CommentListWrap,
  CommentWriteBtn,
  CommentWriter,
} from '../../styles/dashboard/activityComment.styles';

const ActivityCommentSection = (props) => {
    useEffect(()=>{
        let tmp = async () => {
            try{
        axios.get(`/api/comments?activityId=${props.activityId}`)
                setCommentList(res.data);
            }catch(err){
                alert('댓글목록 오류 ');
            }
        }
        tmp();
    },[props.activityId])

  return (
    <section>
      <CommentInputWrap>
        <CommentInput />
        <CommentWriteBtn>댓글달기</CommentWriteBtn>
      </CommentInputWrap>
      <CommentListWrap>
        {
            CommentListWrap.map()
        }
        <CommentItem>
          <CommentHeader>
            <CommentWriter>작성자 id : aa@naver.com</CommentWriter>
            <CommentDate>작성일: 2023-01-01</CommentDate>
            <CommentDate>수정일: 2023-01-01</CommentDate>
            <CommentBtn>삭제</CommentBtn>
          </CommentHeader>
          </CommentItem>
          <Comment>

          </Comment>

      </CommentListWrap>
    </section>
  );
};

export default ActivityCommentSection;
