import { Pagination } from '@mui/material';
import {
  ActivityBody,
  ActivityFooter,
  ActivityInput,
  ActivitySectionHeader,
  ActivitySelect,
  ActivityWriteBtn,
} from '../../styles/dashboard/activity.styles';
import ActivityCard from './activityCard';
import { useEffect, useState } from 'react';
import axios from 'axios';

const ActivitySection = () => {
  const cntPerPage = 4;
  const [activityList, setActivityList] = useState([]);
  const [totalPage, setTotalPage] = useState(1);
  useEffect(() => {
    let tmp = async () => {
      try {
        let res = await axios.get(`/api/activities?order=dateDesc&limit=${cntPerPage}&page=1`);

        setTotalPage(Math.ceil(res.data.total_cnt / cntPerPage));
      } catch (err) {
        console.log(err);
      }
    };
    tmp();
  }, []);

  return (
    <section>
      <ActivitySectionHeader>
        <ActivityInput />
        <ActivitySelect>
          <option>최신순</option>
          <option>오래된순</option>
          <option>좋아요순</option>
          <option>조회수순</option>
        </ActivitySelect>
        <ActivityWriteBtn>글 쓰기</ActivityWriteBtn>
      </ActivitySectionHeader>
      <ActivityBody>
        {
            
        }
          
      </ActivityBody>
      <ActivityFooter>
        <Pagination count={totalPage} />
      </ActivityFooter>
    </section>
  );
};

export default ActivitySection;
