import { useContext, useEffect, useState } from "react";
import { BoardContent, BoardDetailWrap, BoardInfoWrap, BoardTitle, WriteBtn } from "../../styles/dashboard/activityDetail.styles";
import axios from "axios";
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import FavoriteIcon from '@mui/icons-material/Favorite';



const ActivityDetailSection = (props)=>{
    
    const {accessToken} = useContext(UserContext);
    const [activity , setActivity] =useState({
        id:0,
        title:'w',
        content:'d',
        writerEmail:'sdaf',
        createdDate: 'sd',
        updatedDate:'sds',
        view:'0',
        likeCnt:'0',
        liked:'no',
        imgUrl :[]

    })

    useEffect(()=>{
        let tmp = async ()=>{
            if(accessToken === null) return;
            axios.get(`/api/activities/${props.activityId}`, {
                headers:{Authorization:`Bearer ${accessToken}`}
            });
        }

        tmp();
    }, [props.activityId, accessToken]);

    const onLikeClick = ()=>{

    }

    return(
        <section>
            <BoardDetailWrap>
                <BoardTitle>
                    {activity.title}
                    <div style={{display:'flex', alignItems:'baseline'}}>
                        <span>좋아요:{activity.likeCnt} </span>
                        <span onClick={onLikeClick}>
                            {activity.liked === 'yes' ? <FavoriteIcon style={ {color:'red'} }/> : <FavoriteBorderIcon/>}
                        </span>
                    </div>
                </BoardTitle>
                <BoardInfoWrap>
                    <p>작성자</p>
                    <p>{activity.writerEmail}</p>
                    <p>조회수</p>
                    <p>{activity.view}</p>
                </BoardInfoWrap>
                <BoardInfoWrap>
                    <p>작성일자</p>
                    <p>{activity.createdDate}</p>
                    <p>수정일자</p>
                    <p>{activity.updatedDate}</p>
                </BoardInfoWrap>
                <BoardContent>
                    게시물 내용입니다...!!!!
                </BoardContent>
                <div style={{alignSelf:'flex-end', display:'flex', columnGap:'10px'}}>
                    <WriteBtn>수정하기</WriteBtn>
                    <WriteBtn style={{backgroundColor:'red'}}>삭제하기</WriteBtn>
                </div>
            </BoardDetailWrap>
        </section>
    );
}

export default ActivityDetailSection;
