import styled from "@emotion/styled";

export const CardWrap = styled.div`
    box-shadow: 3px 4px 12px rgba(0, 0, 0, 0.08);
    border-radius: 6px;
    overflow: hidden;
    width: 350px;
`;

export const CardImg = styled.div`
    background-image: url(${(props) => props.imgURL === undefined ? 'activity-default.jpg' : props.imgURL} );
    height: 250px;
    background-repeat: no-repeat;
    background-position: center;
    background-size: cover;

    position: relative;
`;

export const CardContent = styled.div`
    padding: 15px 20px;
`;

export const CardTitle = styled.h2`
    font-size: 18px;
    color: black;
    margin: 5px 0;
`;

export const CardDetail = styled.p`
    font-size: 16px;
    color: silver;
    margin: 5px 0;
`;

export const CardCheckBox = styled.input`
    position: absolute;
    top: 20px;
    left: 20px;
`;

export const CardLikeButton = styled.div`
position: absolute;
top: 20px;
left: 20px;
`;



export const BoardDetailWrap = styled.div`
  width: 80%;
  display: flex;
  flex-direction: column;
  margin: 0 auto;
`;

export const BoardTitle = styled.div`
  border-top: 1px solid black;
  border-bottom: 1px solid silver;
  font-size: 18px;
  padding: 20px;
  display: flex;
  background-color: #e9e9e9;
`;

export const BoardInfoWrap = styled.div`
  display: flex;
  border-bottom: 1px solid silver;
  &>p{
    padding: 20px;
  }

  & > p:nth-of-type(odd){
    width: 10%;
    text-align: center;
    border-right: 0.5px solid silver;
    border-left: 0.5px solid silver;
    
  }
  & > p:nth-of-type(even){
    width: 40%;
  }
`;

export const BoardContent = styled.div`

  padding: 40px 20px;
  border-bottom:1px solid silver;
`;
