import { useEffect } from 'react';
import {
    Comment,
    CommentBtn,
  CommentDate,
  CommentHeader,
  CommentInput,
  CommentInputWrap,
  CommentItem,
  CommentListWrap,
  CommentWriteBtn,
  CommentWriter,
} from '../../styles/dashboard/activityComment.styles';

const ActivityCommentSection = (props) => {
    useEffect(()=>{
        let tmp
        axios.get(`/api/comments?activityId=${props.activityId}`)
    },[props.activityId])

  return (
    <section>
      <CommentInputWrap>
        <CommentInput />
        <CommentWriteBtn>댓글달기</CommentWriteBtn>
      </CommentInputWrap>
      <CommentListWrap>
        <CommentItem>
          <CommentHeader>
            <CommentWriter>작성자 id : aa@naver.com</CommentWriter>
            <CommentDate>작성일: 2023-01-01</CommentDate>
            <CommentDate>수정일: 2023-01-01</CommentDate>
            <CommentBtn>삭제</CommentBtn>
          </CommentHeader>
          </CommentItem>
          <Comment>

          </Comment>

      </CommentListWrap>
    </section>
  );
};

export default ActivityCommentSection;
