import EditIcon from '@mui/icons-material/Edit';
import { useState } from 'react';

const CareerRow = (props) => {
    const[isEdit, setIsEdit] = useState(true);

    const dateFormat =() =>{

        dateFormat.replace('년','-').replace('월','-').replace('일','-').replace(/ /g,'')
    };

    const e =props.career;
    const {checkRowId, onSelect, onDeleteRow} = props;

    return(
        <>
        <tr key={e.id}>
                <td>
                  <input
                    type="checkbox"
                    checked={checkedRowId.includes(e.id)}
                    onChange={() => {
                      onSelect(e.id);
                    }}
                  />
                </td>
                <td>{isEdit ? <input defaultValue={dateFormat(e.end_date)} /> :e.company }</td>
                <td>{isEdit ? <input defaultValue={dateFormat(e.end_date)} /> :e.position}</td>
                <td>
                {isEdit ? <input defaultValue={dateFormat(e.end_date)} /> :e.start_date}-{e.end_date}
                </td>
                <td
                  onClick={() => onDeleteRow(e.id)}
                  style={{
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    cursor: 'pointer',
                  }}
                >
                  <DeleteOutlineIcon />
                </td>
              </tr>
        </>
    )
};

export default CareerRow;
