import DeleteOutlineIcon from '@mui/icons-material/DeleteOutline';
import EditIcon from '@mui/icons-material/Edit';
import { useState } from 'react';

const CareerRow = (props) => {
  // 만일 isEdit에 true가 들어있으면 해당 행이 수정 상태임 을 의미하고
  // isEdit에 false가 들어있으면 해당 행이 수정상태가 아님 을 의미한다
  const [isEdit, setIsEdit] = useState(false);

  const dateFormat = (date) => {
    // '2023년10월01일' --> '2023-10-01'
    // / /g --> 모든 띄어쓰기 공백 을 의미
    return date.replace('년', '-').replace('월', '-').replace('일', '').replace(/ /g, '');
  };

  // props.career = e;
  // props.checkedRowId
  const e = props.career;
  const { checkedRowId, onSelect, onDeleteRow } = props;

  // 상태를 수정상태로 변경해주는 함수
  const onEditState = () => {
    setIsEdit(true);
  };


  

  return (
    <tr>
      <td>
        <input
          type="checkbox"
          checked={checkedRowId.includes(e.id)}
          onChange={() => {
            onSelect(e.id);
          }}
        />
      </td>
      <td onClick={onEditState}>{isEdit ? <input defaultValue={e.company} /> : e.company}</td>
      <td onClick={onEditState}>{isEdit ? <input defaultValue={e.position} /> : e.position}</td>
      <td onClick={onEditState} style={{ display: 'flex' }}>
        {isEdit ? <input defaultValue={dateFormat(e.start_date)} type="date" /> : e.start_date}-
        {isEdit ? <input type="date" defaultValue={dateFormat(e.end_date)} /> : e.end_date}
      </td>
      <td
        onClick={() => onDeleteRow(e.id)}
        style={{
          cursor: 'pointer',
        }}
      >
        {isEdit ? <EditIcon /> : <DeleteOutlineIcon />}
      </td>
    </tr>
  );
};

export default CareerRow;
