import { useState } from 'react';
import {
  AuthBody,
  AuthBox,
  AuthForm,
  BgImg,
  CancelIcon,
  ErrMsg,
  InputBoxWrap,
  Wrap,
  Input,
  Select,
  Option,
  Button,
} from '../../styles/auth/auth.styles';

const JoinPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [passwordCheck, setPasswordCheck] = useState('');
  const [question, setQuestion] = useState('1');
  const [answer, setAnswer] = useState('');

  const [emailErrMsg, setEamilErrMsg] = useState('');
  const [passwordErrMsg, setPasswordErrMsg] = useState('');
  const [passwordCheckErrMsg, setPasswordCheckErrMsg] = useState('');
  const [answerErrMsg, setAnsterErrMsg] = useState('');

  const emailInputHandler = (e) => {
    const emailText = e.target.value;
    setEmail(emailText);

    if (email === '') {
      setEamilErrMsg('이메일은 필수입력값입니다.');
    } else if (!email.includes('@')) {
      setEamilErrMsg('이메일 형식을 올바르게 작성해주세요');
    } else {
      setEamilErrMsg('');
    }
  };

  const passwordInputHandler = (e) => {
    const passwordText = e.target.value;
    setPassword(passwordText);

    if (passwordText === '') {
      setPasswordErrMsg('비밀번호는 필수입력칸입니다.');
    } else {
      setPasswordErrMsg('');
    }
  };

  return (
    <>
      <BgImg>
        <Wrap>
          <CancelIcon></CancelIcon>
          <AuthBox>
            <h1>회원가입</h1>
            <AuthBody>
              <AuthForm>
                <InputBoxWrap>
                  <div className="Input-box">
                    <Input onChange={emailInputHandler} type="text" placeholder="아이디" />
                    <ErrMsg>{emailErrMsg}</ErrMsg>
                  </div>
                  <div className="Input-box">
                    <Input type="passWord" placeholder="비밀번호는 필수 입력입니다" />
                    <ErrMsg>{passwordInputHandler}</ErrMsg>
                  </div>
                  <div className="Input-box">
                    <Input type="passWord" placeholder="비밀번호는 필수 입력입니다" />
                    <ErrMsg>{passwordCheckErrMsg}</ErrMsg>
                  </div>
                  <div className="Input-box">
                    <Select>
                      <Option value={1}> 내가 태어난곳은? </Option>
                      <Option value={2}> 어린시절 나의 별명은 ? </Option>
                      <Option value={3}> 나의 강아지 이름은?? </Option>
                    </Select>
                    <Input type="text" placeholder="이메일을 입력해주세요" />
                  </div>
                </InputBoxWrap>
                <Button>회원가입 완료입니다</Button>
              </AuthForm>
            </AuthBody>
          </AuthBox>
        </Wrap>
      </BgImg>
    </>
  );
};

export default JoinPage;
