// ref 익혀보기
import { useRef, useState } from 'react';

const RefTestComponent = () => {
  const [a, setA] = useState(null);
  const b = useRef(0);
  let c = 0;

  const showAll = () => {
    console.log(a);
    console.log(b);
    console.log(c);
  };

  return (
    <>
      <h1>ref테스트</h1>
      <h1>state뱐수 a 의 값: {a}
      <input/></h1>
      <button
        onClick={() => {
          setA(a + 1);
        }}
      ></button>
      <button
        onClick={() => {
          b.current++;
        }}
      ></button>
      <button
        onClick={() => {
          c++;
        }}
      ></button>
      <button onClick={showAll()}></button>
    </>
  );
};

export default RefTestComponent;
