import { Pagination } from '@mui/material';
import {
  ActivityBody,
  ActivityFooter,
  ActivityInput,
  ActivitySectionHeader,
  ActivitySelect,
  ActivityWriteBtn,
} from '../../styles/dashboard/activity.styles';
import ActivityCard from './activityCard';
import { useEffect, useState } from 'react';

const ActivitySection = () => {
  const [activityList, setActivityList] = useState([]);

  useEffect(() => {
    axi.get('/api/activites?order=dateDesc&limit=4&page=1');
  }, []);

  return (
    <section>
      <ActivitySectionHeader>
        <ActivityInput />
        <ActivitySelect>
          <option>최신순</option>
          <option>오래된순</option>
          <option>좋아요순</option>
          <option>조회수순</option>
        </ActivitySelect>
        <ActivityWriteBtn>글 쓰기</ActivityWriteBtn>
      </ActivitySectionHeader>
      <ActivityBody>
        <ActivityCard
          activity={{
            title: '김제희',
            content: 'dfwefqf',
            email: 'aaa@naver.com',
            createdDate: '2023-01-01',
            like: 56,
            imgURL: '이미지주소',
          }}
        />
        <ActivityCard
          activity={{
            title: '강아지 봉사활동',
            content: '봉사활동은 재미있어요',
            email: 'abc@dd.com',
            createdDate: '2023-05-01',
            like: 102,
            imgURL: null,
          }}
        />
      </ActivityBody>
      <ActivityFooter>
        <Pagination count={20} />
      </ActivityFooter>
    </section>
  );
};

export default ActivitySection;
