import { useContext, useEffect, useState } from 'react';
import {
  Comment,
  CommentBtn,
  CommentDate,
  CommentHeader,
  CommentInput,
  CommentInputWrap,
  CommentItem,
  CommentListWrap,
  CommentWriteBtn,
  CommentWriter,
} from '../../styles/dashboard/activityComment.styles';
import axios from 'axios';
import { UserContext } from '../../App';

const ActivityCommentSection = (props) => {
  const [commentList, setCommentList] = useState([]);
  const [content, setContent] = useState('');
  const { accessToken } = useContext(UserContext);

  useEffect(() => {
    let tmp = async () => {
      if (accessToken === null) return;
      try {
        let res = await axios.get(`/api/comments?activityId=${props.activityId}`, {
          headers: { Authorization: `Bearer ${accessToken}` },
        });
        setCommentList(res.data);
      } catch (err) {
        alert('댓글목록 오류');
      }
    };

    tmp();
  }, [props.activityId, accessToken]);

  const onCommentClick = async () => {
    try {
      let res = await axios.post(
        '/api/comments',
        {
          content,
          activityId: props.activityId,
        },
        { headers: { Authorization: `Bearer ${accessToken}` } }
      );
      setCommentList([...commentList, res.data]);
      alert('댓글 추가 성공~~');
    } catch (err) {
      console.error('댓글 추가 중 오류:', err);
      alert('댓글 추가 중 오류 발생..!');
    }
  };

  //댓글 id를 방아서 id를 가진 댓글 삭제
  const onDeletClick = async (id) => {
    let ddd = window.confirm('정말 삭제하시겠습니까?');
    if (ddd === false) return;

    //확인 눌렀을때 실행됨
    //express에게 삭제 요청
    try {
      // await axios.delete('/api/comments', { data: { id } });
      setCommentList(
        commentList.filter((el) => {
          return el.id !== id;
        })
      );
      alert('댓글이 삭제 되었습니다');
    } catch (err) {
      alert('댓글 삭제 실패입니다.');
    }
  };

  return (
    <section>
      <CommentInputWrap>
        <CommentInput
          onChange={(e) => {
            setContent(e.target.value);
          }}
          value={content}
        />
        <CommentWriteBtn onClick={onCommentClick}>댓글달기</CommentWriteBtn>
      </CommentInputWrap>
      <CommentListWrap>
        {commentList.map((comment) => (
          <CommentItem key={comment.id}>
            <CommentHeader>
              <CommentWriter>작성자 id : {comment.writer_email}</CommentWriter>
              <CommentDate>(작성일){comment.created_date}</CommentDate>
              <CommentDate>(수정일){comment.updated_date}</CommentDate>
              {comment.owner && (
                <CommentBtn onClick={() => onDeletClick(comment.id)}>삭제</CommentBtn>
              )}
            </CommentHeader>
            <Comment>{comment.content}</Comment>
          </CommentItem>
        ))}
      </CommentListWrap>
    </section>
  );
};

export default ActivityCommentSection;
