import { useState } from 'react';
import {
  CardContent,
  CardDetail,
  CardImg,
  CardLikeButton,
  CardTitle,
  CardWrap,
} from '../../styles/dashboard/activityCard.styles';
import FavoriteBoderIcon from '@mui/icons-material/Favorite';
import { colors } from '@mui/material';

const ActivityCard = (props) => {
  console.log(props.activity.imgURL);
  const [isLiked, setIsLiked] = useState(false);

  return (
    <CardWrap>
      <CardImg imgURL={props.activity.imgURL}>
        <CardLikeButton
          onClick={() => {
            setIsLiked(!isLiked);
          }}
        >
          {isLiked ? <FavoriteBoderIcon style={{ color: 'red' }} /> : <FavoriteBoderIcon />}
        </CardLikeButton>
      </CardImg>
      <CardContent>
        <CardTitle>{props.activity.title}</CardTitle>
        <CardDetail>{props.activity.content}</CardDetail>
        <CardDetail>작성자 : {props.activity.email}</CardDetail>
        <CardDetail>작성일자 : {props.activity.createdDate}</CardDetail>
        <CardDetail>좋아요 : {props.activity.like}</CardDetail>
        <button>자세히 보기</button>
      </CardContent>
    </CardWrap>
  );
};

export default ActivityCard;
