import styled from '@emotion/styled';

const ConditionalPage = () => {
  return (
    <>
      <h1>조건부 렌더링</h1>
      <MyDiv id="김제희" className="err" abc="1번">
        div1
      </MyDiv>
      <MyDiv id="김제희" className="b" abc="2번">
        div1
      </MyDiv>
      <Div2 qqq="1번">div1</Div2>
      <Div2 qqq="2번">div1</Div2>
      <Div3 bbb="1번">div1</Div3>
      <Div3 bbb="2번">div1</Div3>
    </>
  );
};

const Div3 = styled.div`
  border: 3px solid black;
  width: 200px;
  ${() => {
    return 'backgoound-color:red';
  }}
  ${(props) => {
    return props.bbb === '1번' ? { backgroundColor: 'pink' } : { margin: '10px' };
  }}
`;

const Div2 = styled.div`
  border: 3px solid black;
  width: 200px;
  background-color: ${(a) => (a.qqq === '1번' ? 'red' : 'blue')};
`;

const MyDiv = styled.div`
  border: 3px solid black;
  width: 200px;
  text-align: center;
  ${(a) => (a.abc === '1번' ? `background-color:red` : `background-color:blue`)}
`;

export default ConditionalPage;
